package Operadores;

public class Atribuicao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero=4;
		System.out.println(numero += 2);
		System.out.println(numero -= 2);
		System.out.println(numero *= 2);
		System.out.println(numero /= 2);
		System.out.println(numero %= 2);
	}
}
